import React from 'react'
import { useState, useEffect } from 'react'
import './FileExplorer.css'
import Folder from './Folder.js'
import File from './File.js'

function FileExplorer({ addFolder, addFile }) {
  const [selecteditem, setSelecteditem] = useState();
  const [files, setFiles] = useState([]);

  useEffect(() => {
    getdata();
  }, []);

  function createFolderobject(folderdata) {
    let contents = [];
    folderdata.contents.forEach(content => {
      if(content.type==='folder'){
        contents.push(createFolderobject(content));
      }else if(content.type==='file'){
        contents.push(createFileobject(content));
      }
    })
    return <Folder key={folderdata.id} name={folderdata.name} FolderData={folderdata} onselect={changeSelected} contents={contents} />
  }
  function createFileobject(filedata) {
    return <File key={filedata.id} fname={filedata.name} onselect={changeSelected} FileData={filedata}/>
  }

  function getdata() {
    setFiles([]);
    fetch('http://192.168.0.43:5000/api/get_files')
      .then(response => response.json())
      .then(data => {
        setFiles(data.files);
      })
      .catch(error => console.error(error));
  }

  useEffect(() => {
    if(addFolder){
      if(selecteditem){
        let urls = selecteditem;
        let currfiles = files;
        for(let i=0; i<urls.length; i++){
          let url = urls[i];
          let found = false;
          let index = 0;
          while(!found){
            let file = currfiles[index];
            console.log(file.name, url)
            if(file.name===url){
              found = true;
              currfiles = file.contents;
            }
          }
        } 
        let folderplaceholder = {
          type: 'folderplaceholder',
          id: -1,
          owner: 'admin',
          url: urls.join('/')
        };
        currfiles = [folderplaceholder, ...currfiles];
      }
    }
  }, [addFolder]);

  useEffect(() => {
    if(addFile){
    }
  }, [addFile]);

  const changeSelected = (e) => {
    document.querySelectorAll('.Explorer-Selected').forEach((element) => {
      element.classList.remove('Explorer-Selected');
    });
    let finaltarget = e.target;
    if(finaltarget!==selecteditem){
      while(!finaltarget.classList.contains('Folder-Header') && !finaltarget.classList.contains('File-Header')) {
        finaltarget = finaltarget.parentElement;
      }
      finaltarget.classList.add('Explorer-Selected');
      if(finaltarget.classList.contains('Folder-Header')){
        let hiddencompleteurl = finaltarget.querySelector('.HiddenCompleteUrl').innerHTML;
        let urls = hiddencompleteurl.split('/').slice(1);
        setSelecteditem(urls);
      }
    }else{
      setSelecteditem(null);
    }
  }

  return (
    <div className='FileExplorer'>
      <button className='FileExplorer-Refresh' onClick={getdata}>Refresh</button>
      {files.map((file) => {
        if (file.type === 'folder') {
          return createFolderobject(file);
        } else if(file.type === 'file'){
          return createFileobject(file);
        }
      })}
    </div>
  )
}

export default FileExplorer
