import React from 'react'
import { useState, useEffect } from 'react'
import './FileExplorer.css'
import FolderCreating from './FolderCreating.js'
import Folder from './Folder.js'
import File from './File.js'

function FileExplorer({ addFolder, addFile }) {
  const [selecteditem, setSelecteditem] = useState();
  const [files, setFiles] = useState([]);

  function createFolderobject(folderdata) {
    let contents = [];
    folderdata.contents.forEach(content => {
      if(content.type==='folder'){
        contents.push(createFolderobject(content));
      }else{
        contents.push(createFileobject(content));
      }
    })
    return <Folder key={folderdata.id} name={folderdata.name} Folderid={folderdata.id} onselect={changeSelected} contents={contents} />
  }
  function createFileobject(filedata) {
    return <File key={filedata.id} fname={filedata.name} onselect={changeSelected} fid={filedata.id} />
  }

  const handleAddFolder = (parentFolder) => {

    const newFolder = createFolderobject({
      id: "-1", // Replace with your unique ID generation logic
      name: 'New Folder', // Customize the folder name
      contents: [], // Initially, the new folder is empty
      type: 'folder',
    });
  
    if (parentFolder) {
      // If a parent folder is specified, add the new folder to its contents
      parentFolder.contents.push(newFolder);
      setFiles([...files]);
    } else {
      // If no parent folder is specified, add the new folder to the root level
      setFiles([...files, newFolder]);
    }
  };

  async function getdata() {
    try {
      const response = await fetch('http://192.168.0.43:5000/api/get_files');
      const data = await response.json();
      return data;
    } catch (error) {
      console.error(error);
    }
  }

  function createObjects(data) {
    let tempfiles = [];
    data.files.forEach(file => {
      if(file.type==='folder'){
        tempfiles.push(createFolderobject(file));
      }else{
        tempfiles.push(createFileobject(file));
      }
    })
    if(tempfiles.length===0){
      tempfiles.push(<p key='empty' className='FileExplorer-NothingHere'>Nothing Here</p>);
    }
    setFiles(tempfiles);
  }

  useEffect(async () => {
    let data = await getdata();
    console.log(data);
    createObjects(data);
  }, []);

  useEffect(() => {
    if(addFolder){
      handleAddFolder(selecteditem);
    }
  }, [addFolder]);

  useEffect(() => {
    if(addFile){
      console.log(selecteditem)
    }
  }, [addFile]);

  const changeSelected = (e) => {
    document.querySelectorAll('.Explorer-Selected').forEach((element) => {
      element.classList.remove('Explorer-Selected');
    });
    let finaltarget = e.target;
    if(finaltarget!==selecteditem){
      while(!finaltarget.classList.contains('Folder-Header') && !finaltarget.classList.contains('File-Header')) {
        finaltarget = finaltarget.parentElement;
      }
      finaltarget.classList.add('Explorer-Selected');
      setSelecteditem(finaltarget);
    }else{
      setSelecteditem(null);
    }
  }

  return (
    <div className='FileExplorer'>
      <button className='FileExplorer-Refresh' onClick={getdata}>Refresh</button>
      {files.map((file) => file)}
    </div>
  )
}

export default FileExplorer
