  }

  const ondouble = (e) => {
    openfile(e);
    onselect(e);
  };

  const managefile = (e) => {
    console.log("Hi")
  }
  return (
    <div className='File'>
        <div className='File-Header'  onClick={onselect} onDoubleClick={ondouble}>
            <div className='LHS'>
                <div className='File-Header-Icon'></div>
                <p className='File-Header-Name'>{filename}</p>
            </div>
            <button className='File-Header-Manage' onClick={managefile}></button>
        </div>
    </div>
  )
}

export default File
