import React from 'react'
import { useState, useEffect } from 'react'
import './FileExplorer.css'
import Folder from './Folder.js'
import File from './File.js'

function FileExplorer() {
  const [selecteditem, setSelecteditem] = useState();

  const changeSelected = (e) => {
    document.querySelectorAll('.Explorer-Selected').forEach((element) => {
      element.classList.remove('Explorer-Selected');
    });
    let selecteditem = e.target;
    while(!selecteditem.classList.contains('Folder-Header') && !selecteditem.classList.contains('File-Header')) {
      selecteditem = selecteditem.parentElement;
    }
    selecteditem.classList.add('Explorer-Selected');
    console.log(selecteditem);
  }

  return (
    <div className='FileExplorer'>
      <Folder name='Main' Folderid="0" onselect={changeSelected} contents={[<Folder name="sub" Folderid="1" onselect={changeSelected} contents={[]} />, <Folder name="sub2" Folderid="2" onselect={changeSelected} contents={[<File fname="hello.txt" onselect={changeSelected} fid="3"/>]}/>, <File fname="hello2.txt" onselect={changeSelected} fid="4"/>]} />
    </div>
  )
}

export default FileExplorer
