import React, { useState } from 'react';
import './Folder.css';

function Folder({ name, contents, Folderid }) {
    const [folderid, setFolderid] = useState(Folderid);
    const [foldername, setFoldername] = useState(name);
    const [foldercontents, setFoldercontents] = useState(contents);
    const [showContents, setShowContents] = useState(false);

    const toggleContents = (e) => {
        if(!showContents) {
            e.target.style.transform = 'rotate(90deg)';
        }else{
            e.target.style.transform = 'rotate(0deg)';
        }
        if(showContents) {//is showing
            let subdirs = document.getElementById(folderid);
            let amount = subdirs.childNodes.length * -100;
            subdirs.childNodes.forEach((child) => {
                child.style.transform = 'translateY('+amount+'%)';
            });
            setTimeout(() => {
                setShowContents(false);
            }, 300);
        }else{
            setShowContents(true);
            console.log(folderid, Folderid);
            setTimeout(() => {
                let subdirs = document.getElementById(folderid);
                let amount = subdirs.childNodes.length * 100;
                subdirs.childNodes.forEach((child) => {
                    child.style.transform = 'translateY('+amount+'%)';
                });
            }, 100);
        }
    };

    return (
        <div className='Folder'>
            <div className='Folder-Header'>
                <p className='Folder-Header-Name'>{foldername}</p>
                <button className='Folder-Header-DropDown' onClick={toggleContents}></button>
            </div>
            {showContents && (
                <div className='Folder-Contents' id={folderid}>
                    {foldercontents.map((content, index) => (
                        <div key={index} className='Folder-Filler'>{content}</div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default Folder;
